def update_hospital_data():
    global hospital_data
    while True:
        for hospital in hospital_data:
            delta = random.choice([1, 2, 3, -1, -2, -3, -4])
            if hospital['bedsAvailable'] <= 50:
                hospital['bedsAvailable'] = hospital['bedsAvailable'] + 11
            else:
                hospital['bedsAvailable'] += delta
        time.sleep(1)


@app.route('/hospitals')
def get_hospitals():
    global hospital_data
    for hospital in hospital_data:
        delta = random.choice([1, 2, 3, -1, -2, -3, -4])
        if hospital['bedsAvailable'] <= 50:
            hospital['bedsAvailable'] = hospital['bedsAvailable'] + 11
        else:
            hospital['bedsAvailable'] += delta
    time.sleep(1)
    return jsonify(hospital_data)
